import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Shows from './components/Shows';
import Booking from './components/Booking';
import Payment from './components/Payment';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      const userData = localStorage.getItem('user');
      if (userData) setUser(JSON.parse(userData));
    }
  }, [token]);

  const handleLogin = (userData, userToken) => {
    setUser(userData);
    setToken(userToken);
    localStorage.setItem('token', userToken);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  };

  return (
    <Router>
      <div className="App">
        <header>
          <h1>BookMyShow</h1>
          {user && (
            <div>
              <span>Welcome, {user.name}</span>
              <button onClick={handleLogout}>Logout</button>
            </div>
          )}
        </header>
        <Routes>
          <Route path="/login" element={!user ? <Login onLogin={handleLogin} /> : <Navigate to="/shows" />} />
          <Route path="/shows" element={user ? <Shows token={token} /> : <Navigate to="/login" />} />
          <Route path="/booking/:showId" element={user ? <Booking token={token} /> : <Navigate to="/login" />} />
          <Route path="/payment/:bookingId" element={user ? <Payment token={token} /> : <Navigate to="/login" />} />
          <Route path="/" element={<Navigate to={user ? "/shows" : "/login"} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
